mapper.select:
select

from t_oa_message


        AND ID=#{id}


        AND ADDRESSER=#{addresser}


        AND ADDRESSERORGID=#{addresserorgid}


        AND RECIPIENT=#{recipient}


        AND RECIPIENTORGID=#{recipientorgid}


        AND DATE=#{date}


        AND CONTENT=#{content}


        AND READFLAG=#{readflag}

SELECT IFNULL(MIN(id),0) FROM t_oa_message 
WHERE  
ADDRESSER = #{recipient} 
AND RECIPIENT = #{addresser} 
AND READFLAG = 0
